import React from "react";
import { View, Text, StyleSheet} from 'react-native';
import { useEffect, useState } from "react";

import UserCard2 from "./UserCard2";

const RandomUser2 = () => {
    //This state will hold the data fetched from the API.
    const [Data, setData] = useState(null);

    const getRandomUser = async() => {
        const response = await fetch('https://randomuser.me/api/', {
            method: "GET"
        });
        // Converting the data into an object format
        return await response.json();
    };

    useEffect(() => {
        getRandomUser().then((data) => {
            // console.log(data);
            setData(data.results[0]);
            console.log(data.results[0]);
        });
    }, []);

    return (
        <View style={styles.container}>
            {Data &&
                // names of the props given here are Mytitle and Mybody
                //As the data for this url is an object so we cant use map function
                <UserCard2 key={Data.id} UserData={Data} />}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#f0f0f0',
    },
    noData: {
        fontSize: 16,
        textAlign: 'center',
        margin: 20,
        color: 'red',
    },
});

export default RandomUser2;
